mapper.select:
select 

from DAILY_FLIGHT_MASTER
where FLIGHT_NO_IATA = #{flightNoIata,jdbcType=VARCHAR}
  and OPERATIONAL_DATE = #{operationalDate,jdbcType=TIMESTAMP}
  and A_OR_D = #{aOrD,jdbcType=VARCHAR}
  and FLIGHT_REPEAT_COUNT = #{flightRepeatCount,jdbcType=VARCHAR}

select

from DAILY_FLIGHT_MASTER where 1=1

and to_char(OPERATIONAL_DATE,'yyyy-MM-dd') = #{params.operationalDate,jdbcType=VARCHAR}


FLIGHT_NO_IATA = #{params.flightNoIata,jdbcType=VARCHAR}

mapper.delete:
delete from DAILY_FLIGHT_MASTER
where FLIGHT_NO_IATA = #{flightNoIata,jdbcType=VARCHAR}
  and OPERATIONAL_DATE = #{operationalDate,jdbcType=TIMESTAMP}
  and A_OR_D = #{aOrD,jdbcType=VARCHAR}
  and FLIGHT_REPEAT_COUNT = #{flightRepeatCount,jdbcType=VARCHAR}
