mapper.select:
select * from MOBILE_CHECKIN where 1=1

   and userId=#{params.userId,jdbcType=VARCHAR}


  and devicetoken=#{params.devicetoken,jdbcType=VARCHAR}


  and devicetype=#{params.devicetype,jdbcType=VARCHAR}

order by id desc

mapper.update:
update MOBILE_CHECKIN
set   loginTime=#{loginTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=VARCHAR}

mapper.select:
SELECT count(1)
FROM sys_role
WHERE name = #{name}

  AND parent_id = #{parentId}


  AND id != #{id}
