mapper.select:
select
title,
advise, sender, companyName,stand, send_Time,receiver,A_OR_D,OPERATIONAL_DATE,OPERATIONAL_DATE_D,
flight_NO_IATA flight_NO_IATA_A,flight_NO_IATA_D,receive_Time,status
from task_status where 1=1

  and flight_no_iata=#{params.flight_no_iata,jdbcType=VARCHAR}


  and receiver=#{params.receiver,jdbcType=VARCHAR}


  and to_char(OPERATIONAL_DATE,'yyyy-MM-dd') = #{params.query_date,jdbcType=VARCHAR}

order by send_Time desc

mapper.update:
update TASK_STATUS


  set status = #{status,jdbcType=INTEGER},
  receive_Time=#{receive_Time,jdbcType=TIMESTAMP}


  set status = #{status,jdbcType=INTEGER}

where FLIGHT_NO_IATA = #{flight_NO_IATA_A,jdbcType=VARCHAR}
and  to_char(OPERATIONAL_DATE,'yyyy-MM-dd HH24:MI:SS')=#{OPERATIONAL_DATE,jdbcType=VARCHAR}

mapper.select:
SELECT count(1)
FROM sys_role
WHERE name = #{name}

  AND parent_id = #{parentId}


  AND id != #{id}

SELECT FROM sys_role WHERE id IN
#{id}
order by role_level
