  AREA = #{area,jdbcType=VARCHAR},
  MANUAL = #{manual,jdbcType=VARCHAR}
where FLNO = #{flno,jdbcType=VARCHAR} AND EXEC_DATE = #{execDate,jdbcType=DATE}

update T_EARLY_DEICING
set STAND = #{stand,jdbcType=TIMESTAMP}
where FLNO = #{flno,jdbcType=VARCHAR} AND EXEC_DATE = #{execDate,jdbcType=DATE}
