update.foreach:
UPDATE sys_acl_module
SET module_level = #{sysAclModule.moduleLevel}
WHERE id = #{sysAclModule.id}

mapper.select:
SELECT count(1)
FROM sys_acl_module
WHERE name = #{name}

  AND parent_id = #{parentId}


  AND id != #{id}

SELECT count(1)
FROM sys_acl_module
WHERE parent_id = #{aclModuleId}
